import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Ggwash_profile from './Components/Ggwash_profile';
import Personal_blog from './Components/Personal_blog';
import Linkedin from './Components/Linkedin'
import Github from './Components/Github'
import Picture from './Components/Picture';
import {Posts} from './Components/Posts';
import Postlist from './Components/Postlist';
import SearchBox from './Components/SearchBox';


class App extends Component {
  constructor () {
    super()
    this.state = {
      Posts: Posts,
      searchfield: ''
    }
  }

componentDidMount() {
  fetch('https://jsonplaceholder.typicode.com/posts')
    .then(response=> response.json())
    .then(posts => this.setState({Posts: posts}));
}


  onSearchChange =(event) => {
    console.log(event.target.value);
    this.setState({ searchfield: event.target.value })

  }
  render() {

    const filteredPosts = this.state.Posts.filter(Posts => {
      return Posts.title.toLowerCase().includes(this.state.searchfield.toLowerCase())
    })
    console.log(filteredPosts);
   return (

    <div className="f1 tc">
 	<h3> This is now my home page. </h3>
     <SearchBox searchChange={this.onSearchChange}/>
     <Ggwash_profile />
     <Postlist Posts={filteredPosts} />
     <Linkedin />
     <Github />
     <Picture />
     </div>

    );
  };
}
export default App;
